from deasy_client import Deasy

client = Deasy()
tag = client.tags.create(
    tag_data={
        "name": "name",
        "output_type": "output_type",
    },
)
print(tag.tag_name)

from deasy_client import Deasy

client = Deasy()
response = client.tags.upsert(
    tag_data={
        "name": "name",
        "output_type": "output_type",
    },
)
print(response.available_values_added)

from deasy_client import Deasy

client = Deasy()
tag_response = client.tags.update(
    tag_data={
        "name": "name",
        "output_type": "output_type",
    },
)
print(tag_response.tag_name)

